import{_ as i,a as t}from"./app.893f8b81.js";var s="/blog/assets/dvcs.1d4a02b9.png";const n={},e=t('<h2 id="git-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#git-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> Git \u662F\u4EC0\u4E48</h2><p>Git \u662F\u4E00\u4E2A\u5F00\u6E90\u7684\u5206\u5E03\u5F0F\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\uFF0C\u7528\u4E8E\u654F\u6377\u9AD8\u6548\u5730\u5904\u7406\u4EFB\u4F55\u6216\u5C0F\u6216\u5927\u7684\u9879\u76EE\u3002</p><p>Git \u662F Linus Torvalds \u4E3A\u4E86\u5E2E\u52A9\u7BA1\u7406 Linux \u5185\u6838\u5F00\u53D1\u800C\u5F00\u53D1\u7684\u4E00\u4E2A\u5F00\u653E\u6E90\u7801\u7684\u7248\u672C\u63A7\u5236\u8F6F\u4EF6\u3002</p><p>Git \u4E0E\u5E38\u7528\u7684\u7248\u672C\u63A7\u5236\u5DE5\u5177 CVS\uFF0CSubversion \u7B49\u4E0D\u540C\uFF0C\u5B83\u91C7\u7528\u4E86\u5206\u5E03\u5F0F\u7248\u672C\u5E93\u7684\u65B9\u5F0F\uFF0C\u4E0D\u5FC5\u670D\u52A1\u5668\u7AEF\u8F6F\u4EF6\u652F\u6301\u3002</p><p>Git \u4E0D\u4EC5\u4EC5\u662F\u4E2A\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\uFF0C\u5B83\u4E5F\u662F\u4E2A\u5185\u5BB9\u7BA1\u7406\u7CFB\u7EDF\uFF08CMS\uFF09\uFF0C\u5DE5\u4F5C\u7BA1\u7406\u7CFB\u7EDF\u7B49\u3002</p><p><img src="'+s+'" alt="dvcs" loading="lazy"></p><h2 id="git-\u7684\u8BDE\u751F" tabindex="-1"><a class="header-anchor" href="#git-\u7684\u8BDE\u751F" aria-hidden="true">#</a> Git \u7684\u8BDE\u751F</h2><p>\u5F88\u591A\u4EBA\u90FD\u77E5\u9053\uFF0CLinus Torvalds \u5728 1991 \u5E74\u521B\u5EFA\u4E86\u5F00\u6E90\u7684 Linux\uFF0C\u4ECE\u6B64\uFF0CLinux \u7CFB\u7EDF\u4E0D\u65AD\u53D1\u5C55\uFF0C\u5DF2\u7ECF\u6210\u4E3A\u6700\u5927\u7684\u670D\u52A1\u5668\u7CFB\u7EDF\u8F6F\u4EF6\u4E86\u3002</p><p>Linus Torvalds \u867D\u7136\u521B\u5EFA\u4E86 Linux\uFF0C\u4F46 Linux \u7684\u58EE\u5927\u662F\u9760\u5168\u4E16\u754C\u70ED\u5FC3\u7684\u5FD7\u613F\u8005\u53C2\u4E0E\u7684\uFF0C\u8FD9\u4E48\u591A\u4EBA\u5728\u4E16\u754C\u5404\u5730\u4E3A Linux \u7F16\u5199\u4EE3\u7801\uFF0C\u90A3 Linux \u7684\u4EE3\u7801\u662F\u5982\u4F55\u7BA1\u7406\u7684\u5462\uFF1F</p><p>\u4E8B\u5B9E\u662F\uFF0C\u5728 2002 \u5E74\u4EE5\u524D\uFF0C\u4E16\u754C\u5404\u5730\u7684\u5FD7\u613F\u8005\u628A\u6E90\u4EE3\u7801\u6587\u4EF6\u901A\u8FC7 diff \u7684\u65B9\u5F0F\u53D1\u7ED9 Linus Torvalds\uFF0C\u7136\u540E\u7531 Linus Torvalds \u672C\u4EBA\u901A\u8FC7\u624B\u5DE5\u65B9\u5F0F\u5408\u5E76\u4EE3\u7801\uFF01</p><p>\u4F60\u4E5F\u8BB8\u4F1A\u60F3\uFF0C\u4E3A\u4EC0\u4E48 Linus Torvalds \u4E0D\u628A Linux \u4EE3\u7801\u653E\u5230\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\u91CC\u5462\uFF1F\u4E0D\u662F\u6709 CVS\u3001SVN \u8FD9\u4E9B\u514D\u8D39\u7684\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\u5417\uFF1F\u56E0\u4E3ALinus Torvalds\u575A\u5B9A\u5730\u53CD\u5BF9 CVS \u548C SVN\uFF0C\u8FD9\u4E9B\u96C6\u4E2D\u5F0F\u7684\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\u4E0D\u4F46\u901F\u5EA6\u6162\uFF0C\u800C\u4E14\u5FC5\u987B\u8054\u7F51\u624D\u80FD\u4F7F\u7528\u3002\u6709\u4E00\u4E9B\u5546\u7528\u7684\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\uFF0C\u867D\u7136\u6BD4 CVS\u3001SVN \u597D\u7528\uFF0C\u4F46\u90A3\u662F\u4ED8\u8D39\u7684\uFF0C\u548C Linux \u7684\u5F00\u6E90\u7CBE\u795E\u4E0D\u7B26\u3002</p><p>\u4E0D\u8FC7\uFF0C\u5230\u4E86 2002 \u5E74\uFF0CLinux \u7CFB\u7EDF\u5DF2\u7ECF\u53D1\u5C55\u4E86\u5341\u5E74\u4E86\uFF0C\u4EE3\u7801\u5E93\u4E4B\u5927\u8BA9 Linus Torvalds \u5F88\u96BE\u7EE7\u7EED\u901A\u8FC7\u624B\u5DE5\u65B9\u5F0F\u7BA1\u7406\u4E86\uFF0C\u793E\u533A\u7684\u5F1F\u5144\u4EEC\u4E5F\u5BF9\u8FD9\u79CD\u65B9\u5F0F\u8868\u8FBE\u4E86\u5F3A\u70C8\u4E0D\u6EE1\uFF0C\u4E8E\u662F Linus Torvalds \u9009\u62E9\u4E86\u4E00\u4E2A\u5546\u4E1A\u7684\u7248\u672C\u63A7\u5236\u7CFB\u7EDF BitKeeper\uFF0CBitKeeper \u7684\u4E1C\u5BB6BitMover \u516C\u53F8\u51FA\u4E8E\u4EBA\u9053\u4E3B\u4E49\u7CBE\u795E\uFF0C\u6388\u6743 Linux \u793E\u533A\u514D\u8D39\u4F7F\u7528\u8FD9\u4E2A\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\u3002</p><p>\u5B89\u5B9A\u56E2\u7ED3\u7684\u5927\u597D\u5C40\u9762\u5728 2005 \u5E74\u5C31\u88AB\u6253\u7834\u4E86\uFF0C\u539F\u56E0\u662F Linux \u793E\u533A\u725B\u4EBA\u805A\u96C6\uFF0C\u4E0D\u514D\u6CBE\u67D3\u4E86\u4E00\u4E9B\u6881\u5C71\u597D\u6C49\u7684\u6C5F\u6E56\u4E60\u6C14\u3002\u5F00\u53D1 Samba \u7684 Andrew \u8BD5\u56FE\u7834\u89E3 BitKeeper \u7684\u534F\u8BAE\uFF08\u8FD9\u4E48\u5E72\u7684\u5176\u5B9E\u4E5F\u4E0D\u53EA\u4ED6\u4E00\u4E2A\uFF09\uFF0C\u88AB BitMover \u516C\u53F8\u53D1\u73B0\u4E86\uFF08\u76D1\u63A7\u5DE5\u4F5C\u505A\u5F97\u4E0D\u9519\uFF01\uFF09\uFF0C\u4E8E\u662F BitMover \u516C\u53F8\u6012\u4E86\uFF0C\u8981\u6536\u56DE Linux \u793E\u533A\u7684\u514D\u8D39\u4F7F\u7528\u6743\u3002</p><p>Linus Torvalds \u53EF\u4EE5\u5411 BitMover \u516C\u53F8\u9053\u4E2A\u6B49\uFF0C\u4FDD\u8BC1\u4EE5\u540E\u4E25\u683C\u7BA1\u6559\u5F1F\u5144\u4EEC\uFF0C\u55EF\uFF0C\u8FD9\u662F\u4E0D\u53EF\u80FD\u7684\u3002\u5B9E\u9645\u60C5\u51B5\u662F\u8FD9\u6837\u7684\uFF1A</p><p>Linus Torvalds \u82B1\u4E86\u4E24\u5468\u65F6\u95F4\u81EA\u5DF1\u7528 C \u5199\u4E86\u4E00\u4E2A\u5206\u5E03\u5F0F\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\uFF0C\u8FD9\u5C31\u662F Git\uFF01\u4E00\u4E2A\u6708\u4E4B\u5185\uFF0CLinux \u7CFB\u7EDF\u7684\u6E90\u7801\u5DF2\u7ECF\u7531 Git \u7BA1\u7406\u4E86\uFF01\u725B\u662F\u600E\u4E48\u5B9A\u4E49\u7684\u5462\uFF1F\u5927\u5BB6\u53EF\u4EE5\u4F53\u4F1A\u4E00\u4E0B\u3002</p><p>Git \u8FC5\u901F\u6210\u4E3A\u6700\u6D41\u884C\u7684\u5206\u5E03\u5F0F\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\uFF0C\u5C24\u5176\u662F 2008 \u5E74\uFF0CGitHub \u7F51\u7AD9\u4E0A\u7EBF\u4E86\uFF0C\u5B83\u4E3A\u5F00\u6E90\u9879\u76EE\u514D\u8D39\u63D0\u4F9B Git \u5B58\u50A8\uFF0C\u65E0\u6570\u5F00\u6E90\u9879\u76EE\u5F00\u59CB\u8FC1\u79FB\u81F3 GitHub\uFF0C\u5305\u62EC jQuery\uFF0CPHP\uFF0CRuby \u7B49\u7B49\u3002</p><p>\u5386\u53F2\u5C31\u662F\u8FD9\u4E48\u5076\u7136\uFF0C\u5982\u679C\u4E0D\u662F\u5F53\u5E74 BitMover \u516C\u53F8\u5A01\u80C1 Linux \u793E\u533A\uFF0C\u53EF\u80FD\u73B0\u5728\u6211\u4EEC\u5C31\u6CA1\u6709\u514D\u8D39\u800C\u8D85\u7EA7\u597D\u7528\u7684 Git \u4E86\u3002</p><h2 id="gitub-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#gitub-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> Gitub \u662F\u4EC0\u4E48</h2><p>GitHub \u662F\u4E00\u4E2A\u9762\u5411\u5F00\u6E90\u53CA\u79C1\u6709\u8F6F\u4EF6\u9879\u76EE\u7684\u6258\u7BA1\u5E73\u53F0\uFF0C\u56E0\u4E3A\u53EA\u652F\u6301 Git \u4F5C\u4E3A\u552F\u4E00\u7684\u7248\u672C\u5E93\u683C\u5F0F\u8FDB\u884C\u6258\u7BA1\uFF0C\u6545\u540D GitHub\u3002</p><p>GitHub \u4E8E 2008 \u5E74 4 \u6708 10 \u65E5\u6B63\u5F0F\u4E0A\u7EBF\uFF0C\u9664\u4E86 Git \u4EE3\u7801\u4ED3\u5E93\u6258\u7BA1\u53CA\u57FA\u672C\u7684 Web \u7BA1\u7406\u754C\u9762\u4EE5\u5916\uFF0C\u8FD8\u63D0\u4F9B\u4E86\u8BA2\u9605\u3001\u8BA8\u8BBA\u7EC4\u3001\u6587\u672C\u6E32\u67D3\u3001\u5728\u7EBF\u6587\u4EF6\u7F16\u8F91\u5668\u3001\u534F\u4F5C\u56FE\u8C31\uFF08\u62A5\u8868\uFF09\u3001\u4EE3\u7801\u7247\u6BB5\u5206\u4EAB\uFF08Gist\uFF09\u7B49\u529F\u80FD\u3002\u76EE\u524D\uFF0C\u5176\u6CE8\u518C\u7528\u6237\u5DF2\u7ECF\u8D85\u8FC7 350 \u4E07\uFF0C\u6258\u7BA1\u7248\u672C\u6570\u91CF\u4E5F\u662F\u975E\u5E38\u4E4B\u591A\uFF0C\u5176\u4E2D\u4E0D\u4E4F\u77E5\u540D\u5F00\u6E90\u9879\u76EE Ruby on Rails\u3001jQuery\u3001python \u7B49\u3002 2018 \u5E74 6 \u6708 4 \u65E5\uFF0C\u5FAE\u8F6F\u5BA3\u5E03\uFF0C\u901A\u8FC7 75 \u4EBF\u7F8E\u5143\u7684\u80A1\u7968\u4EA4\u6613\u6536\u8D2D\u4EE3\u7801\u6258\u7BA1\u5E73\u53F0 GitHub\u3002</p><div class="custom-container tip"><p class="custom-container-title">\u4E86\u89E3</p><p>GitHub \u4F7F\u7528 git \u5206\u5E03\u5F0F\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\uFF0C\u800C git \u6700\u521D\u662F LinusTorvalds \u4E3A\u5E2E\u52A9 Linux \u5F00\u53D1\u800C\u521B\u9020\u7684\uFF0C\u5B83\u9488\u5BF9\u7684\u662F Linux \u5E73\u53F0\uFF0C\u56E0\u6B64 git \u548C Windows \u4ECE\u6765\u4E0D\u662F\u6700\u597D\u7684\u670B\u53CB\uFF0C\u56E0\u4E3A\u5B83\u4E00\u70B9\u4E5F\u4E0D\u50CF Windows\u3002GitHub \u53D1\u5E03\u4E86 GitHub for Windows\uFF0C\u4E3A Windows \u5E73\u53F0\u5F00\u53D1\u8005\u63D0\u4F9B\u4E86\u4E00\u4E2A\u6613\u4E8E\u4F7F\u7528\u7684 Git \u56FE\u5F62\u5BA2\u6237\u7AEF\u3002 GitHub for Windows \u662F\u4E00\u4E2A Metro \u98CE\u683C\u5E94\u7528\u7A0B\u5E8F\uFF0C\u96C6\u6210\u4E86\u81EA\u5305\u542B\u7248\u672C\u7684 Git\uFF0Cbash \u547D\u4EE4\u884C shell\uFF0CPowerShell \u7684 posh-git \u6269\u5C55\u3002GitHub \u4E3A Windows \u7528\u6237\u63D0\u4F9B\u4E86\u4E00\u4E2A\u57FA\u672C\u7684\u56FE\u5F62\u524D\u7AEF\u53BB\u5904\u7406\u5927\u90E8\u5206\u5E38\u7528\u7248\u672C\u63A7\u5236\u4EFB\u52A1\uFF0C\u53EF\u4EE5\u521B\u5EFA\u7248\u672C\u5E93\uFF0C\u5411\u672C\u5730\u7248\u672C\u5E93\u9012\u4EA4\u8865\u4E01\uFF0C\u5728\u672C\u5730\u548C\u8FDC\u7A0B\u7248\u672C\u5E93\u4E4B\u95F4\u540C\u6B65\u3002\u5FAE\u8F6F\u4E5F\u901A\u8FC7 CodePlex \u5411\u5F00\u53D1\u8005\u63D0\u4F9B git \u7248\u672C\u63A7\u5236\u7CFB\u7EDF\uFF0C\u800C GitHub \u521B\u9020\u4E86\u4E00\u4E2A\u66F4\u5177\u6709\u5438\u5F15\u529B\u7684 Windows \u7248\u672C\u3002</p></div>',21);function a(r,u){return e}var d=i(n,[["render",a]]);export{d as default};
